var e="ZITADEL Identity and Access Management",t="Most applications need to know the identity of a user allowing to securely store user data in the cloud and provide the same personalised experience across all of the user's devices.",a="ZITADEL's authentication provides backend services, easy-to-use SDKs, and ready-made UI libraries to authenticate users in your applicqtion. It supports authentication using passwords and applies additional security wirh the help of a second factor, for example OTP, to ensure a safe and secure access.",n="ZITADEL's authentication leverages industry standards like OAuth 2.0 and OpenID Connect so that it can be easily integrated in your custom backend.",s="Learn how to start",o="Manuals",i="Developer & Integration",r="Open ZITADEL Console",d={title:e,description:t,description2:a,description3:n,introlink:"Learn how to start",manualslink:"Manuals",devintlink:"Developer & Integration",toconsole:"Open ZITADEL Console"};export default d;export{t as description,a as description2,n as description3,i as devintlink,s as introlink,o as manualslink,e as title,r as toconsole};
